import React from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';

import { inputChange, createTodo } from '../../redux/actions';

const ListHeader = styled.div`
	background-color: #605F5F;
	padding: 30px 40px;
	color: white;
	text-align: center;

  &:after {
    content: "";
    display: table;
    clear: both;
  }
`;

const InputWrapper = styled.div`
  display: flex;
`;

const ListInput = styled.input`
	margin: 0;
	border: none;
	border-radius: 0;
	font-size: 0.8em;
	width: 75%;
	padding: 10px;
	color: #000;
`;

const ListButton = styled.span`
  border-radius: 0;
	padding: 10px;
	width: 25%;
	background-color: #d9d9d9;
	color: #555;
	text-align: center;
	cursor: pointer;
	transition: 0.3s;
	font-size: 0.8em;
`;

class TodoHeader extends React.Component {

}

export default connect(
	null,
	{ inputChange, createTodo }
)(TodoHeader);
