import React, { Fragment } from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';

import { inputChange, createTodo } from '../../redux/actions';

const ListHeader = styled.div`
	background-color: #605F5F;
	padding: 30px 40px;
	color: white;
	text-align: center;

  &:after {
    content: "";
    display: table;
    clear: both;
  }
`;

const FormWrapper = styled.form`
  display: flex;
`;

const InputWrapper = styled.div`
	display: flex;
	flex-wrap: wrap;
	width: 75%;
`;

const TodoInput = styled.input`
	margin: 10px;
	border: none;
	border-radius: 15px;
	font-size: 0.8em;
	padding: 10px;
	color: #000;
	width: 100%;
`;

const NameInput = styled.input`
	margin: 10px;
	border: none;
	border-radius: 15px;
	font-size: 0.8em;
	padding: 10px;
	color: #000;
`

const CheckboxLabel = styled.label`
	align-self: center;
	font-size: 0.9em;
	margin: 10px;
	padding: 10px;
`

const CheckboxInput = styled.input`
	margin: 0 10px;
`;

const AddButton = styled.span`
  margin: 10px;
  border-radius: 15px;
	padding: 10px;
	width: 25%;
	background-color: #d9d9d9;
	color: #555;
	text-align: center;
	cursor: pointer;
	transition: 0.3s;
	font-size: 0.8em;
	align-self: center;
`;

class TodoHeader extends React.Component {
  render() {
		return (
			<ListHeader>
				<h1>To Do List</h1>
				<FormWrapper>
					<InputWrapper>
					  <TodoInput 
							type="text"
							onChange={(event) => this.props.inputChange(event.target.value)}
							value={this.props.todo.input}
							placeholder='What needs to be done?' 
						/>
						<NameInput type="text" placeholder="Name"/>
						<CheckboxLabel>
							Completed Task?
							<CheckboxInput type="checkbox" />
						</CheckboxLabel>
					</InputWrapper>
					<AddButton onClick={() => {
							this.props.createTodo()
							this.props.inputChange('');
					  }}>
						Add
					</AddButton>
				</FormWrapper>
		  </ListHeader>
		)
	}
}
const mapStateToProps = (state) => {
	return { 
		todo: state.todo
	}
}

export default connect(
	mapStateToProps,
	{ inputChange, createTodo }
)(TodoHeader);
